package com.wq.activemq;import javax.jms.JMSException;public interface ActivemqFactory {    /**     * 创建消息生产者     * */    void createMessageProducer(String id,int type,String name) throws JMSException;    /**     * 发送消息     * */    void sendMessage(String id,String message) throws JMSException;    /**     * 创建消息消费者     */    void CreateMessageConsumer(String id,int type,String name) throws JMSException;    /**     * 消息接受     * */    String acceptMessage(String id) throws JMSException;}