package com.wq.hadoop;import net.minidev.json.JSONObject;import java.io.Serializable;import java.util.List;import java.util.Map;/** * @author wengqian */public class BaseReturnVO implements Serializable {    private String msg;    private List<Map<String, Object>> objList;    private List<Map<String, String>> strList;    public BaseReturnVO(String msg) {        this.msg = msg;    }    public BaseReturnVO(List<Map<String, Object>> objList,String str) {        this.objList = objList;    }    public BaseReturnVO(List<Map<String, String>> strList) {        this.strList = strList;    }    public String getMsg() {        return msg;    }    public void setMsg(String msg) {        this.msg = msg;    }    public List<Map<String, Object>> getObjList() {        return objList;    }    public void setObjList(List<Map<String, Object>> objList) {        this.objList = objList;    }    public List<Map<String, String>> getStrList() {        return strList;    }    public void setStrList(List<Map<String, String>> strList) {        this.strList = strList;    }    @Override    public String toString() {        JSONObject object = new JSONObject();        object.put("data",this);        return object.toString();    }}