package com.wq.controller;import com.wq.util.RedisUtil;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.RestController;/** * Created by wengqian on 2018/9/11. */@RestControllerpublic class RedisController {    @Autowired    RedisUtil redisUtil;    /**     * 根据传入的key从redis上获取对应不同的缓存数据     * @param key 缓存的key     * @return Object 返回对象     *     * */    @RequestMapping(value = "/getValue")    public Object getValue(@RequestParam String key) {        return redisUtil.getValue(key);    }}