package com.wq.configureconfigclient01;import org.springframework.beans.factory.annotation.Value;import org.springframework.cloud.context.config.annotation.RefreshScope;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;/** * Created by wengqian on 2018/7/3. */@RestController@RefreshScopepublic class HellController {    @Value("${foo}")    String foo;    @RequestMapping(value = "/hi")    public String hi(){        return foo;    }    @Value("${foo1}")    String foo1;    @RequestMapping(value = "/hi2")    public String hi1(){        return foo1;    }}