package com.wq.microservicefeignclient.feign;import com.wq.microservicefeignclient.entity.User;import com.wq.microservicefeignclient.feign.feignback.FeignClientHystrix;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.RequestParam;/** * Created by wengqian on 2018/6/20. * @author wengqian */@org.springframework.cloud.openfeign.FeignClient(name ="MICRO-SERVICE",fallback = FeignClientHystrix.class)public interface FeignClient {    /**     * @param name 参数     * @return string     * */    @RequestMapping(value = "hello",method= RequestMethod.GET)    String hello(@RequestParam("name") String name);    @RequestMapping(value = "hello1",method= RequestMethod.POST)    String hello(@RequestBody User user);}