package com.wq.microservice1.controller;import com.wq.microservice1.entity.User;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.RestController;/** * Created by wengqian on 2018/6/15. */@RestControllerpublic class HelloController {    @RequestMapping(value = "/hello")    public String hello( @RequestParam String name) {        return "hello "+name+"，this is first messge(micro-service1)";    }    @RequestMapping(value = "/hello1")    public String hello( @RequestBody User user) {        return "hello "+user.getName()+"，this is first messge(micro-service1)";    }}